import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { Route, Routes } from 'react-router-dom';
import Header from './components/Header/Header';
import Cart from './components/Pages/Cart/Cart';
import NotFound from './components/Pages/NotFound/NotFound';
import PizzaBlockContainer from './components/Pages/Pizza-block/PizzaBlockContainer';
import './scss/app.scss';
import { changeCategory } from './state/pizzaBlock-reducer';

export const SearchContext = React.createContext();

function App({store}) {
  const dispatch = useDispatch();
  
  console.log(store.pizzaBlock);
  dispatch(changeCategory(1));
  const [searchValue, setSearchValue] = useState('')
  return (
    <div className="wrapper">
      <SearchContext.Provider value={{searchValue, setSearchValue}}>
        <Header/>
        <div className="content">
          <Routes>
            <Route path='/' element={<PizzaBlockContainer activeCategory={store.pizzaBlock.category}/>} />
            <Route path='/cart' element={<Cart />} />
            <Route path='*' element={<NotFound />} />
          </Routes>
        </div>
      </SearchContext.Provider>
    </div>
  );
}

export default App;
