import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

const initialState = {
  pizzasItems: []
};

const pizzasReducer = createSlice({
  name: 'pizzasItem',
  initialState,
  reducers: {
    setPizzasItems: (state, action) => {
      state.pizzasItems = action.payload
    }
  }
});

const filterFetch = createAsyncThunk(
  'fetchPizzaItems',
  async (searchValue, pageNumber, pizzaCategory, sortParam, sortNumber) => {
    let search = searchValue !== '' ? `name=${searchValue}` : '';
    let pagination = `page=${pageNumber}&limit=4`;
    let category = pizzaCategory === 0 ? '' : `category=${pizzaCategory}`;
    let sortBy = `sortBy=${sortParam[sortNumber]}`;
    //setIsLoad(false)
    try {
      let response = await axios.get(`https://62a5c5ab430ba53411cc40f9.mockapi.io/items?${category}&${search}&${sortBy}&${pagination}`)

      dispatch(setPizzasItems(response.data))
      //setPizzas(response.data);      
    } catch (err) {
      alert(`Не удалось загрузить пиццы`)
    } finally {
      //setIsLoad(true);
    }
  }
)



export const { setPizzasItems } = pizzasReducer.actions;
export default pizzasReducer.reducer;
