import { createSlice } from '@reduxjs/toolkit';

const CHANGE_CATEGORY = 'pizzaBlockReducer/CHANGE_CATEGORY';

const initialState = {
  category: 0,
  sortNumber: 0,
  sortParam: ['rating', 'rating&order=desc', 'price', 'price&order=desc', 'name', 'name&order=desc']
}

export const pizzaState = createSlice({
  name: 'pizzaState',
  initialState,
  reducers: {
    changeCategory: (state, action) => {
      state.category = action.payload
    },
    changeParamSort: (state, action) => {
      console.log(action)
      state.sortNumber = action.payload
    }
  }
});

export const {changeCategory, changeParamSort} = pizzaState.actions;
export default pizzaState.reducer

