const CHANGE_CATEGORY = 'pizzaBlockReducer/CHANGE_CATEGORY';

const initialState = {
  category: 0,
}

export const pizzaState = createSlice({
  name: 'pizzaState',
  initialState,
  reducers: {
    changeCategory: (state, action) => {
      alert(state.category);
      //state.category = action.category
    }
  }
});

export const {changeCategory} = pizzaState.actions;
export default pizzaState.reducer

