import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { showingTotalPrice } from "../../../state/cart-reducer";

const PizzaBlock = ({id, name, price, imageUrl, sizes, types }) => {
  const [pizzaCount, setPizzaCount] = useState(0);
  const [pizzaSize, setPizzaSize] = useState(0);
  const [pizzaType, setPizzaType] = useState(0)
  const pizzaCategory = ['тонкое', 'традиционное'];
  const dispatch = useDispatch();
  
  console.log(count)

  const onAddPizza = () => {
    let count = 1;
    let item = {id, name, price,imageUrl, size: sizes[pizzaSize], type: pizzaCategory[pizzaType], count}
    dispatch(showingTotalPrice(item))
    setPizzaCount(pizzaCount + 1);
  }
  return (
    <div className="pizza-block-wrap">
      <div className="pizza-block">
        <img
          className="pizza-block__image"
          src={imageUrl}
          alt="Pizza"
        />
        <h4 className="pizza-block__title">{name}</h4>
        <div className="pizza-block__selector">
          <ul>
            {types.map((e, i) => <li key={i} onClick={() => setPizzaType(i)} className={pizzaType === i ? "active" : ''}>{pizzaCategory[e]}</li>)}
          </ul>
          <ul>
            {sizes.map((s, i) => <li key={i} onClick={() => setPizzaSize(i)} className={pizzaSize === i ? "active" : ''}>{s} см.</li>)}
          </ul>
        </div>
        <div className="pizza-block__bottom">
          <div className="pizza-block__price">от {price} ₽</div>
          <button className="button button--outline button--add" onClick={onAddPizza}>
            <svg
              width="12"
              height="12"
              viewBox="0 0 12 12"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M10.8 4.8H7.2V1.2C7.2 0.5373 6.6627 0 6 0C5.3373 0 4.8 0.5373 4.8 1.2V4.8H1.2C0.5373 4.8 0 5.3373 0 6C0 6.6627 0.5373 7.2 1.2 7.2H4.8V10.8C4.8 11.4627 5.3373 12 6 12C6.6627 12 7.2 11.4627 7.2 10.8V7.2H10.8C11.4627 7.2 12 6.6627 12 6C12 5.3373 11.4627 4.8 10.8 4.8Z"
                fill="white"
              />
            </svg>
            <span>Добавить</span>
            {count > 0 && <i>{count}</i>}
          </button>
        </div>
      </div>
    </div>
  )
};

export default PizzaBlock;
