import Categories from '../../Categories/Categories';
import PizzaBlock from './Pizza-block';
import Sort from '../../Sort/Sort';
import { useEffect, useState } from 'react';
import SkeletonLoader from '../../../assets/SkeletonReact/SkeletonReact';
import * as axios from 'axios'



const PizzaBlockContainer = (props) => {
  const [pizzas, setPizzas] = useState([]);
  const [isLoad, setIsLoad] = useState(false);

  useEffect(() => {
    fetch('https://62a5c5ab430ba53411cc40f9.mockapi.io/items')
      .then(response => response.json())
      .then(response => {
        setPizzas(response);
        setIsLoad(true);
      })
    window.scrollTo(0, 0);
  }, []);

  const onSortClick = (i) => {
    let param = ['rating', 'price', 'name'];
    setIsLoad(false)
    axios.get(`https://62a5c5ab430ba53411cc40f9.mockapi.io/items?sortBy=${param[i]}`)
      .then(response => {
        setPizzas(response.data);

      })
    setIsLoad(true)
  }
  console.log(pizzas)
  return (
    <div className="container">
      <div className="content__top">
        <Categories />
        <Sort onSortClick={onSortClick} />
      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">
        {!isLoad
          ? [...new Array(6)].map((_, i) => <SkeletonLoader key={i} className="pizza-block" />)
          : pizzas.map(obj => {
            return <PizzaBlock key={obj.id} {...obj} />
          })
        }


      </div>
    </div>
  )
};
export default PizzaBlockContainer;
