import { createSlice } from '@reduxjs/toolkit';

const CHANGE_CATEGORY = 'pizzaBlockReducer/CHANGE_CATEGORY';

const initialState = {
  category: 0,
}

const pizzaState = createSlice({
  name: 'pizzaState',
  initialState,
  reducers: {
    changeCategory: (state, action) => {
      state.category = action.category
    }
  }
})
const pizzaBlockReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_CATEGORY:
      return {
        ...state,
        category: action.category
      }
    default:
      return state;
  }
};

export const changeCategory = (category) => ({type: CHANGE_CATEGORY, category});



export default pizzaBlockReducer;
