  const item = useSelector(itemPizzaSelector);
  console.log(item)
  const dispatch = useDispatch()
  const params = useParams();
  useEffect(() => {
    dispatch(getFullPizza(params.id))
  }, [])
  return (  
    <div className="container">
      { item === undefined && alert(item.length)}
