const Categories = (props) => {
  const categories = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые'];
  const onActiveCategories = (num) => {
    alert(1)
  }
  return (
    <div className="categories">
      <ul>
        <li onClick={() => onActiveCategories()} className="active">Все</li>
        <li  onClick={() => onActiveCategories()}>Мясные</li>
        <li  onClick={() => onActiveCategories()}>Вегетарианская</li>
        <li  onClick={() => onActiveCategories()}>Гриль</li>
        <li  onClick={() => onActiveCategories()}>Острые</li>
        <li  onClick={() => onActiveCategories()}>Закрытые</li>
      </ul>
    </div>
  )
};

export default Categories;
