import { useState } from "react";

const Categories = (props) => {
  const [activeCategory, setActiveCategory] = useState(0);
  const categories = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые'];
  const onActiveCategories = (num) => {
    setActiveCategory(num)
  }
  alert(activeCategory)
  return (
    <div className="categories">
      <ul>
        <li onClick={() => onActiveCategories(0)} className="active">Все</li>
        <li  onClick={() => onActiveCategories(1)}>Мясные</li>
        <li  onClick={() => onActiveCategories(2)}>Вегетарианская</li>
        <li  onClick={() => onActiveCategories(3)}>Гриль</li>
        <li  onClick={() => onActiveCategories(4)}>Острые</li>
        <li  onClick={() => onActiveCategories(5)}>Закрытые</li>
      </ul>
    </div>
  )
};

export default Categories;
