import { useDispatch } from "react-redux";
import { changeCategory } from "../../state/pizzaBlock-reducer";

const Categories = ({onCategoryClick, activeCategory}) => {
 const dispatch = useDispatch();
 const onClickCategory = (i) => {
  dispatch(changeCategory(i))
 }
  const categories = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые'];
  const onActiveCategories = (num) => {
    onCategoryClick(num)
  }
  return (
    <div className="categories">
      <ul>
        {categories.map((e, i) => {
          return (
            <li key={i} onClick={(i) => onClickCategory(i)} className={activeCategory === i ? "active" : ''} >{e}</li>
          )
        })}
      </ul>
    </div>
  )
};

export default Categories;
