const initialState = {
  item: []
};

const fullPizzaReducer = createSlice({
  name: 'fullPizza',
  initialState,
  reducers: {

  },
  extraReducers: builder => {
    builder.addCase(getFullPizza.fulfilled, (state, action) => {
      state.item = action.payload
    })
  }
});

export const getFullPizza = createAsyncThunk(
  `fullPizza/getFullPizza`,
  async (id) => {
    debugger
